def getVirtualKeysCode(*vks):
    keyCodes = []
    for vk in vks:
        keyCode = Keys.getVirtualKeyCode(vk)
        if keyCode is None:
            Trace.log("Manager", 0, "Keys: wrong vk '{}', fix and try again".format(vk))
            return
        keyCodes.append(keyCode)
    return keyCodes

def getVirtualKeyCode(vk):
    if vk is None:
        return
    keyCode = VK_MAP.get(vk, None)

    if keyCode is None:
        trace_msg = "Keys: can't find vk key by input {!r}".format(vk)
        if str(vk).startswith("VK_") is False:
            trace_msg += ". Looks like you forgot prefix 'VK_'"
        Trace.log("Manager", 0, trace_msg)

    return keyCode